    console.log('- Approved KYC:', approvedKYC);
    console.log('- Refer Back KYC:', referBackKYC);
    console.log('- Rejected KYC:', rejectedKYC);

    return {
      totalFarmers,
      totalEmployees,
      unassignedFarmers,
      pendingKYC,
      approvedKYC,
      referBackKYC,
      rejectedKYC,
