import React, { useState, useEffect } from 'react';
import { fpoAPI } from '../api/apiService';

const FPOBoardMembersModal = ({ isOpen, onClose, fpoId, fpoName }) => {
  const [boardMembers, setBoardMembers] = useState([]);
  const [loading, setLoading] = useState(false);
  const [searchTerm, setSearchTerm] = useState('');
  const [dateRange, setDateRange] = useState('');
  const [showCreateForm, setShowCreateForm] = useState(false);
  const [editingMember, setEditingMember] = useState(null);

  // Form state for creating/editing board members
  const [formData, setFormData] = useState({
    name: '',
    designation: '',
    phoneNumber: '',
    email: '',
    linkedinProfileUrl: '',
    location: '',
    role: 'MEMBER'
  });

  useEffect(() => {
    if (isOpen && fpoId) {
      loadBoardMembers();
    }
  }, [isOpen, fpoId]);

  const loadBoardMembers = async () => {
    try {
      setLoading(true);
      const response = await fpoAPI.getFPOBoardMembers(fpoId);
      setBoardMembers(response.data || []);
    } catch (error) {
      console.error('Error loading board members:', error);
    } finally {
      setLoading(false);
    }
  };

  const handleCreateMember = async (e) => {
    e.preventDefault();
    try {
      await fpoAPI.addBoardMember(fpoId, formData);
      setShowCreateForm(false);
      setFormData({
        name: '',
        designation: '',
        phoneNumber: '',
        location: '',
        email: '',
        role: 'MEMBER'
      });
      loadBoardMembers();
    } catch (error) {
      console.error('Error creating board member:', error);
    }
  };

  const handleEditMember = (member) => {
    setEditingMember(member);
    setFormData({
      name: member.name || '',
      designation: member.designation || '',
      phoneNumber: member.phoneNumber || '',
      location: member.location || '',
      email: member.email || '',
      role: member.role || 'MEMBER'
    });
    setShowCreateForm(true);
  };

  const handleUpdateMember = async (e) => {
    e.preventDefault();
    try {
      await fpoAPI.updateBoardMember(fpoId, editingMember.id, formData);
      setShowCreateForm(false);
      setEditingMember(null);
      setFormData({
        name: '',
        designation: '',
        phoneNumber: '',
        location: '',
        email: '',
        role: 'MEMBER'
      });
      loadBoardMembers();
    } catch (error) {
      console.error('Error updating board member:', error);
    }
  };

  const handleDeleteMember = async (memberId) => {
    if (window.confirm('Are you sure you want to delete this board member?')) {
      try {
        await fpoAPI.removeBoardMember(fpoId, memberId);
        loadBoardMembers();
      } catch (error) {
        console.error('Error deleting board member:', error);
      }
    }
  };

  const filteredMembers = boardMembers.filter(member => {
    const matchesSearch = member.name?.toLowerCase().includes(searchTerm.toLowerCase()) ||
                         member.designation?.toLowerCase().includes(searchTerm.toLowerCase()) ||
                         member.phoneNumber?.includes(searchTerm);
    return matchesSearch;
  });

  if (!isOpen) return null;

  return (
    <div className="modal-overlay">
      <div className="modal-content board-members-modal">
        <div className="modal-header">
          <h2>Board Members List</h2>
          <button className="close-button" onClick={onClose}>×</button>
        </div>

        <div className="modal-body">
          {/* Create Board Members Button */}
          <div className="create-section">
            <button 
              className="create-button"
              onClick={() => {
                setShowCreateForm(true);
                setEditingMember(null);
                setFormData({
                  name: '',
                  designation: '',
                  phoneNumber: '',
                  location: '',
                  email: '',
                  role: 'MEMBER'
                });
              }}
            >
              + Create Board Members
            </button>
          </div>

          {/* Filter Section */}
          <div className="filter-section">
            <div className="filter-label">
              <span>Filter</span>
            </div>
            <div className="filter-inputs">
              <input
                type="text"
                placeholder="Search here..."
                value={searchTerm}
                onChange={(e) => setSearchTerm(e.target.value)}
                className="search-input"
              />
              <div className="date-range-container">
                <input
                  type="text"
                  placeholder="Enter a date range"
                  value={dateRange}
                  onChange={(e) => setDateRange(e.target.value)}
                  className="date-range-input"
                />
                <span className="calendar-icon">📅</span>
                <div className="date-format-hint">MM/DD/YYYY - MM/DD/YYYY</div>
              </div>
            </div>
          </div>

          {/* Table */}
          <div className="table-container">
            <table className="board-members-table">
              <thead>
                <tr>
                  <th>Id</th>
                  <th>Name</th>
                  <th>Designation</th>
                  <th>Phone number</th>
                  <th>Location</th>
                  <th>Status</th>
                  <th>Actions</th>
                </tr>
              </thead>
              <tbody>
                {loading ? (
                  <tr>
                    <td colSpan="7" className="loading-cell">Loading...</td>
                  </tr>
                ) : filteredMembers.length === 0 ? (
                  <tr>
                    <td colSpan="7" className="no-data-cell">No data matching the filter</td>
                  </tr>
                ) : (
                  filteredMembers.map((member, index) => (
                    <tr key={member.id || index}>
                      <td>{member.id || `BM${index + 1}`}</td>
                      <td>{member.name || '-'}</td>
                      <td>{member.designation || '-'}</td>
                      <td>{member.phoneNumber || '-'}</td>
                      <td>{member.location || '-'}</td>
                      <td>
                        <span className={`status-badge ${member.status?.toLowerCase() || 'active'}`}>
                          {member.status || 'Active'}
                        </span>
                      </td>
                      <td>
                        <div className="action-buttons">
                          <button 
                            className="edit-btn"
                            onClick={() => handleEditMember(member)}
                          >
                            Edit
                          </button>
                          <button 
                            className="delete-btn"
                            onClick={() => handleDeleteMember(member.id)}
                          >
                            Delete
                          </button>
                        </div>
                      </td>
                    </tr>
                  ))
                )}
              </tbody>
            </table>
          </div>
        </div>

        {/* Create/Edit Form Modal */}
        {showCreateForm && (
          <div className="form-modal-overlay">
            <div className="form-modal-content">
              <div className="form-modal-header">
                <h3>{editingMember ? 'Edit Board Member' : 'Create Board Member'}</h3>
                <button 
                  className="close-button" 
                  onClick={() => {
                    setShowCreateForm(false);
                    setEditingMember(null);
                  }}
                >
                  ×
                </button>
              </div>
              <form onSubmit={editingMember ? handleUpdateMember : handleCreateMember}>
                <div className="form-grid">
                  <div className="form-group">
                    <label>Name *</label>
                    <input
                      type="text"
                      value={formData.name}
                      onChange={(e) => setFormData({...formData, name: e.target.value})}
                      required
                    />
                  </div>
                  <div className="form-group">
                    <label>Designation *</label>
                    <input
                      type="text"
                      value={formData.designation}
                      onChange={(e) => setFormData({...formData, designation: e.target.value})}
                      required
                    />
                  </div>
                  <div className="form-group">
                    <label>Phone Number *</label>
                    <input
                      type="tel"
                      value={formData.phoneNumber}
                      onChange={(e) => setFormData({...formData, phoneNumber: e.target.value})}
                      required
                    />
                  </div>
                  <div className="form-group">
                    <label>Location *</label>
                    <input
                      type="text"
                      value={formData.location}
                      onChange={(e) => setFormData({...formData, location: e.target.value})}
                      required
                    />
                  </div>
                  <div className="form-group">
                    <label>Email</label>
                    <input
                      type="email"
                      value={formData.email}
                      onChange={(e) => setFormData({...formData, email: e.target.value})}
                    />
                  </div>
                  <div className="form-group">
                    <label>Role</label>
                    <select
                      value={formData.role}
                      onChange={(e) => setFormData({...formData, role: e.target.value})}
                    >
                      <option value="CHAIRMAN">Chairman</option>
                      <option value="VICE_CHAIRMAN">Vice Chairman</option>
                      <option value="SECRETARY">Secretary</option>
                      <option value="TREASURER">Treasurer</option>
                      <option value="MEMBER">Member</option>
                    </select>
                  </div>
                </div>
                <div className="form-actions">
                  <button type="button" onClick={() => setShowCreateForm(false)}>
                    Cancel
                  </button>
                  <button type="submit" className="submit-btn">
                    {editingMember ? 'Update' : 'Create'}
                  </button>
                </div>
              </form>
            </div>
          </div>
        )}
      </div>
    </div>
  );
};

export default FPOBoardMembersModal;
